.dot-pattern {
  position: relative;
  overflow: hidden;
  background: #000;
}

.dot-pattern::before {
  content: '';
  position: absolute;
  inset: 0;
  background-image: radial-gradient(#6366f1 2px, transparent 2px);
  background-size: 24px 24px;
  opacity: 0.3;
}

.dot-pattern::after {
  content: '';
  position: absolute;
  inset: 0;
  background: radial-gradient(
    ellipse at center,
    transparent 0%,
    rgba(0,0,0,0.8) 100%
  );
}
